mapper.update:
update member
set name = #{name},  HIREDATE = #{hiredate},
	deptcode = #{deptCode}, POSCODE = #{posCode},  salary = #{salary}
where memno = #{memNo}

mapper.select:
select count(*) from member


		memNo=#{customerId}

	and hiredate >=#{startDay}

	and hiredate < to_date(#{endDay})+1

mapper.update:
update member
set firedate = #{firedate}
where memno = #{memNo}
