mapper.update:
update payment
set hasFile=#{hasFile}
where docNo=#{docNo}

update payment
set formNo=#{formNo},typeNo=#{typeNo},title=#{title},content=#{content},keep=#{keep},
expirydate=add_months(sysdate,#{keep}), writedate=sysdate, imsy=#{imsy},progress=#{progress}
where docNo=#{docNo} and memNo=#{writememNo}

update paylistView
set gmemNo=NULL
where docNo=#{docNo}

update payment
set imsy='Y'
where docNo=#{docNo}

mapper.select:
select distinct docNo,memNo,formNo,typeNo,title,hasFile,
	writedate,expirydate,typeName,name,imsy from paylistView

		imsy='N'
		and memNo=#{identNum}

			and title=#{searchKeyword}


			and typeNo=#{searchCondition}


			and to_date(writedate)>=to_date(#{startDay})


			and to_date(writedate) < to_date(#{endDay})+1


			and sysdate < expirydate+1
			order by docNo desc

select * from paymentview

	imsy='Y'
	and memNo=#{identNum}

		and title=#{searchKeyword}


		and typeNo=#{searchCondition}


		and to_date(writedate)>=to_date(#{startDay})


		and to_date(writedate) < to_date(#{endDay})+1


		and sysdate < expirydate+1
		order by docNo desc
