mapper.select:
select *
from
(
    select rownum as RNUM, A.*
    from(
        select * from SpayViewVO


				MemNo=#{MemNo}

			and TICREGDATE>=#{startDay}

			and TICREGDATE < to_date(#{endDay})+1 

		order by TICNO desc
    )A    
)

where RNUM>#{firstRecordIndex}
  and RNUM<=#{firstRecordIndex }+ #{recordCountPerPage}

select count(*) from SpayView


		MemNo=#{MemNo}

	and ticRegDate>=#{startDay}

	and ticRegDate < to_date(#{endDay})+1 

order by ticno desc
